"use strict";
let counter =document.getElementById("counter")
function _toConsumableArray(a) {
  if (Array.isArray(a)) {
    for (let b = 0, c = Array(a.length); b < a.length; b++) c[b] = a[b];
    return c;
  }
  return Array.from(a);
}
let playing = !0,
  timer = function () {
    return setInterval(function () {
     
      b = parseInt(counter.innerText);
      counter.innerText = b + 1;
    }, 1e3);
  },
  interval = timer(),
  minus = document.getElementById("minus"),
  plus = document.getElementById("plus"),
  heart = document.getElementById("heart"),
  pause = document.getElementById("pause"),
  commentForm = document.getElementsByTagName("form")[0];
minus.addEventListener("click", function () {
 
  b = parseInt(counter.innerText);
  counter.innerText = b - 1;
}),
  plus.addEventListener("click", function () {
    
    b = parseInt(counter.innerText);
    counter.innerText = b + 1;
  }),
  heart.addEventListener("click", function () {
    let list = document.createElement("li");
    list.innerHTML = `${counter.innerText} has been liked `;
    document.querySelector(".likes").append(list);

    //list.innerHTML = `${counter.innerHTML} has been liked `;
    //document.querySelector(".likes").append(list);

  }
  )
  pause.addEventListener("click", function () {
    playing
      ? ((playing = !1), clearInterval(interval), (this.innerText = "resume"))
      : ((playing = !0), (interval = timer()), (this.innerText = "pause")),
      []
        .concat(_toConsumableArray(document.getElementsByTagName("button")))
        .forEach(function (a) {
          "pause" !== a.id && (a.disabled = !playing);
        });
  }),
  commentForm.addEventListener("submit", function (event) {
    event.preventDefault();
    let input = document.getElementById("comment-input");
    let list = document.createElement("li");
    let ul = document.createElement("ul");

    list.innerHTML = `${input.value}`;
    ul.appendChild(list);




    let b = this.children[0],
      c = b.value;
    b.value = "";
    let d = document.querySelector(".comments"),
      e = document.createElement("p");
    (e.innerText = c), d.appendChild(e);
  });